    function objectFlip(obj) {
        const ret = {};
        Object.keys(obj).forEach(key => ret[obj[key]] = key);
        return ret;
    }

    function objectFilter(obj, fx = i => i) {
        const ret = {};
        for (const key of Object.keys(obj)) {
            if (fx(key))
                ret[key] = obj[key];
        }
        return ret;
    }

